package com.leetcode.problems.string.repeatedsubstringpattern;

public class Lc459 {
    public static void main(String[] args) {
        //String s = "";
        String s = "abab";
        System.out.println(new Solution().repeatedSubstringPattern(s));

    }
}

class Solution {

    //方法一：暴力破解
    public boolean repeatedSubstringPattern1(String s) {
        int n = s.length();
        for (int i = 1; i <= n / 2; i++) {
            if (n % i != 0) {
                continue;
            }
            String str = s.substring(0, i);
            int times = n / i;
            StringBuilder sb = new StringBuilder();
            for (int j = 0; j < times; j++) {
                sb.append(str);
            }
            if (sb.toString().equals(s)) {
                return true;
            }
        }
        return false;
    }

    //方法二：kmp
    public boolean repeatedSubstringPattern(String s) {
        int n = s.length();
        int[] next = new int[n];
        getNext(next, s);
        //next[n - 1]是指最长相等前后缀的长度；n - (next[n - 1])是指重复的字符串的长度
        //例如abcdabcdabcd   next[n - 1] = 8;n - (next[n - 1]) = 4，即abcd的长度
        if (next[n - 1] > 0 && n % (n - (next[n - 1])) == 0) {
            return true;
        }
        return false;
    }

    private void getNext(int[] next, String s) {
        int j = 0;
        next[j] = 0;
        for (int i = 1; i < next.length; i++) {
            while (j > 0 && s.charAt(i) != s.charAt(j)) {
                j = next[j - 1];
            }
            if (s.charAt(i) == s.charAt(j)) {
                j++;
            }
            next[i] = j;
        }
    }
}
